mapper.select:
select *from cls s
left join college g on  s.collegeId=g.clgId
where clsId =#{clsId}

select *from cls s
left join college g on  s.collegeId=g.clgId where 1=1

    and clsId= #{clsId}


    and  clsName=#{clsName}


    and  clsNum=#{clsNum}

mapper.update:
UPDATE book.cls
 SET clsNum = #{clsNum}, clsName = #{clsName}, collegeId = #{collegeId},
 WHERE clsId = #{clsId};
